import React, { useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from "react-router-dom";
import { FaCalendarAlt, FaMapMarkerAlt } from "react-icons/fa";

const UnitSelector = ({ onChange }) => {
    const [unit, setUnit] = useState('');
    const handleChange = (e) => {
        setUnit(e.target.value);
        if (onChange) onChange(e.target.value);
    };
    return (
        <div>
            <label>
                <input type="radio" name="unit" value="year" checked={unit === 'year'} onChange={handleChange} /> 연도
            </label>
            <label >
                <input type="radio" name="unit" value="month" checked={unit === 'month'} onChange={handleChange} /> 월
            </label>
            <label>
                <input type="radio" name="unit" value="day" checked={unit === 'day'} onChange={handleChange} /> 일
            </label>
            <label>
                <input type="radio" name="unit" value="time" checked={unit === 'time'} onChange={handleChange} /> 시간
            </label>
        </div>
    );
};

const MetaSearchFilter = ({ onFilterChange }) => {
    const [searchType, setSearchType] = useState(''); // 'location' or 'time'
    const [radius, setRadius] = useState(10);
    const [unit, setUnit] = useState('');
    const navigate = useNavigate();

    // 검색 기준 변경
    const handleSearchTypeChange = (e) => {
        setSearchType(e.target.value);
        // 초기화
        setRadius(10);
        setUnit('');
        if (onFilterChange) onFilterChange({ searchType: e.target.value });
    };

    // 반경 변경
    const handleRadiusChange = (e) => {
        const value = Number(e.target.value);
        setRadius(value);
        if (onFilterChange) onFilterChange({ searchType, radius: value });
    };

    // 단위 변경
    const handleUnitChange = (selectedUnit) => {
        setUnit(selectedUnit);
        if (onFilterChange) onFilterChange({ searchType, unit: selectedUnit });
    };

    return (
        <div>
            <ResultWrap>
                <h1>🎛️ 검색 필터 설정하기</h1>
                <FilterCard>
                    <Section>
                        <Label>
                            <FaCalendarAlt style={{ marginRight: 6, color: '#4a90e2' }}/>
                            검색 기준 선택
                        </Label>
                        <div>
                            <label>
                                <input
                                    type="radio"
                                    name="searchType"
                                    value="location"
                                    checked={searchType === 'location'}
                                    onChange={handleSearchTypeChange}
                                /> 위치
                            </label>
                            <label style={{ marginLeft: 16 }}>
                                <input
                                    type="radio"
                                    name="searchType"
                                    value="time"
                                    checked={searchType === 'time'}
                                    onChange={handleSearchTypeChange}
                                /> 시간
                            </label>
                        </div>
                    </Section>

                    {searchType === 'location' && (
                        <Section>
                            <Label>
                                <FaMapMarkerAlt style={{ marginRight: 6, color: '#ff5a5f' }}/> 위치 기반 반경
                            </Label>
                            <RadiusRow>
                                <RadiusInput
                                    type="range"
                                    min={1}
                                    max={100}
                                    value={radius}
                                    onChange={handleRadiusChange}
                                    accentColor="#ff5a5f"
                                />
                                <RadiusValue>{radius} km</RadiusValue>
                            </RadiusRow>
                        </Section>
                    )}

                    {searchType === 'time' && (
                        <Section>
                            <Label>
                                <FaCalendarAlt style={{ marginRight: 6, color: '#4a90e2' }}/> 해당 사진과 
                            </Label>
                            <UnitSelector onChange={handleUnitChange} />
                        </Section>
                    )}
                </FilterCard>
            </ResultWrap>
            <ButtonContainer>
                <FloatingButton onClick={() => navigate("/metasearch")}>
                    📸 + 이미지 검색하기
                </FloatingButton>
            </ButtonContainer>
        </div>
    );
};

const ResultWrap = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: center;
    margin-top: 30px;
`;


const FilterCard = styled.div`
    display: flex;
    flex-direction: column;
    gap: 24px;
    padding: 32px 28px 24px 28px;
    background: white;
    border-radius: 20px;
    box-shadow: 0 8px 24px rgba(74, 144, 226, 0.08), 0 1.5px 4px rgba(0,0,0,0.03);
    border: 1px solid #e0e7ef;
    max-width: 660px;
    //min-width: 320px;
    width: 100%;
`;

const Section = styled.div`
    display: flex;
    flex-direction: column;
`;

const Label = styled.label`
    font-weight: 600;
    margin-bottom: 10px;
    color: #333;
    font-size: 1.08rem;
    display: flex;
    align-items: center;
`;



const RadiusRow = styled.div`
    display: flex;
    align-items: center;
    gap: 16px;
`;

const RadiusInput = styled.input`
    width: 100%;
    accent-color: #ff5a5f;
`;

const RadiusValue = styled.div`
    min-width: 50px;
    font-size: 1rem;
    color: #ff5a5f;
    font-weight: 700;
    text-align: right;
`;

const ButtonContainer = styled.div`
    display: flex;
    flex-direction: column; 
    align-items: flex-end;
    padding: 20px 40px 0 0;
`;

const FloatingButton = styled.button`
    align-self: flex-end;
    margin: 50px 0;
    background-color: #000000;
    color: white;
    border: none;
    border-radius: 12px;
    padding: 12px 15px;
    font-size: 0.9rem;
    cursor: pointer;
    transition: background-color 0.3s ease;

    &:hover {
        background-color: rgb(55, 58, 61);
    }
`;

export default MetaSearchFilter;
