import { useContext, useEffect, useState } from "react";
import axios from "axios";
import { FileContext } from "../contexts/FileContext";
import { useNavigate } from "react-router-dom";
import styled from 'styled-components';
import DateTimeFilter from "../components/_function/DateTimeFilter"

function Metadata() {
    const { selectedFile } = useContext(FileContext);
    const [metadata, setMetadata] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        console.log("selectedFile:", selectedFile);

        if (!selectedFile) return;

        const formData = new FormData();
        formData.append("file", selectedFile);

        // 비동기 함수 선언 후 즉시 실행
        const fetchMetadata = async () => {
            try {
                const response = await axios.post('http://localhost:8080/api/upload', formData, {
                    headers: {
                        "Content-Type": "multipart/form-data"
                    }
                });
                setMetadata(response.data);  // GPS 정보와 메타데이터 표시
            } catch (error) {
                console.error("메타데이터 추출 오류:", error);
            }
        };

        fetchMetadata();
    }, [selectedFile]);

    return (
        <div>
            <h2>이미지 메타데이터 추출</h2>
            <ResultWrap>

                <h3>📸 업로드 된 이미지</h3>
                {selectedFile ? (
                    <img
                        src={URL.createObjectURL(selectedFile)}
                        alt="업로드된 이미지"
                        style={{ maxWidth: 300, marginBottom: 16 }}
                    />
                ) : (
                    <div
                        style={{
                            width: 300,
                            height: 200,
                            backgroundColor: '#ccc',
                            marginBottom: 16,
                            display: 'flex',
                            justifyContent: 'center',
                            alignItems: 'center',
                            color: '#666',
                            borderRadius: 8,
                            fontSize: '1rem',
                        }}
                    >
                        이미지 없음
                    </div>
                )}


                <h3>📸 추출된 EXIF 메타데이터</h3>
                {metadata && (
                    <pre>{JSON.stringify(metadata, null, 2)}</pre>
                )}

            </ResultWrap>
            <ButtonContainer>
                {/*<DateTimeFilter/>*/}
            <FloatingButton onClick={() => navigate("/metasearch")}>
                📸 + 이미지 검색하기
            </FloatingButton>
            </ButtonContainer>
        </div>
    );
}

const ResultWrap = styled.div`
  flex: 1;
  display: flex;
flex-direction: column; 
gap: 20px;
  overflow-y: auto;
  position: relative; 
`;
const ButtonContainer = styled.div`
  flex: 1;
  display: flex;
flex-direction: column; 
gap: 20px;
  padding: 20px;
  overflow-y: auto;
  position: relative; 
`;

const FloatingButton = styled.button`
  align-self: flex-end;
  margin: 50px 0;
  background-color: #000000;
  color: white;
  border: none;
  border-radius: 12px;
  padding: 12px 15px;
  font-size: 0.9rem;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: rgb(55, 58, 61);
  }
`;
export default Metadata;
