import { useContext, useEffect, useState } from "react";
import axios from "axios";
import styled from 'styled-components';
import { useLocation } from "react-router-dom";

function MetaSearch() {
    const location = useLocation();

    const [result, setResults] = useState(null);
    const [error, setError] = useState(null);

    const [loading, setLoading] = useState(true);

    const fileName = location.state?.fileName;
    const radius = location.state?.radius;
    const searchType = location.state?.searchType;
    const unit = location.state?.unit;

    useEffect(() => {
        const fetchResults = async () => {
            try {
                setLoading(true);
                const response = await axios.get("http://localhost:8080/api/search", {
                    params: {
                        fileName,
                        radius,
                        searchType,
                        unit
                    }
                });
                console.log("Response Data: ", response.data);
                setResults(response.data);
                setLoading(false);
            } catch (err) {
                setError("ê²€ìƒ‰ ê²°ê³¼ë¥¼ ë¶ˆëŸ¬ì˜¤ì§€ ëª»í–ˆìŠµë‹ˆë‹¤.");
                setLoading(false);
            }
        };
        fetchResults();
    }, [fileName, radius, searchType, unit]);



    return (
        <ResultWrap>
            <h1>ðŸ”Ž ê²€ìƒ‰ ê²°ê³¼</h1>
            <FilterCard>
                {loading && <InfoText>ë¡œë”© ì¤‘...</InfoText>}
                {!loading && error && <ErrorText>{error}</ErrorText>}
                {!loading && !error && result?.length === 0 && (
                    <InfoText>ê²€ìƒ‰ ê²°ê³¼ê°€ ì—†ìŠµë‹ˆë‹¤.</InfoText>
                )}
                {!loading && !error &&
                    <div>
                    {result.map((image) => (
                            <div key={image.id}>
                                <img
                                    src={`http://localhost:8080/upload/${image.fileName}`}
                                    alt={image.fileName}
                                    style={{ width: "200px", height: "200px", objectFit: "cover" }}
                                />
                            </div>
                        ))}
                    </div>
                }
            </FilterCard>
        </ResultWrap>
    );
}

const ResultWrap = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column; 
    align-items: center;
    margin-top: 30px;
`;

const FilterCard = styled.div`
    display: flex;
    flex-direction: column;
    gap: 18px;
    padding: 32px 28px 24px 28px;
    background: white;
    border-radius: 20px;
    box-shadow: 0 8px 24px rgba(74, 144, 226, 0.08), 0 1.5px 4px rgba(0,0,0,0.03);
    border: 1px solid #e0e7ef;
    max-width: 660px;
    width: 100%;
    margin-bottom: 50px;
`;

const ResultItem = styled.div`
    display: flex;
    align-items: center;
    gap: 18px;
    padding: 14px 0;
    border-bottom: 1px solid #f1f4fa;
    &:last-child {
        border-bottom: none;
    }
`;

const Thumb = styled.img`
    width: 72px;
    height: 72px;
    object-fit: cover;
    border-radius: 12px;
    border: 1px solid #e0e7ef;
    background: #fafbfc;
`;

const MetaInfo = styled.div`
    display: flex;
    flex-direction: column;
    gap: 4px;
    font-size: 1rem;
    color: #222;
`;

const InfoText = styled.div`
    color: #888;
    text-align: center;
    margin: 30px 0;
`;

const ErrorText = styled.div`
    color: #ff4d4f;
    text-align: center;
    margin: 30px 0;
    font-weight: bold;
`;
export default MetaSearch;
