import { useContext, useEffect, useState } from "react";
import axios from "axios";
import { FileContext } from "../contexts/FileContext";
import { useNavigate } from "react-router-dom";
import styled from 'styled-components';

function Metadata() {
    const { selectedFile } = useContext(FileContext);
    const [metadata, setMetadata] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        console.log("selectedFile:", selectedFile);

        if (!selectedFile) return;

        const formData = new FormData();
        formData.append("file", selectedFile);

        // ÎπÑÎèôÍ∏∞ Ìï®Ïàò ÏÑ†Ïñ∏ ÌõÑ Ï¶âÏãú Ïã§Ìñâ
        const fetchMetadata = async () => {
            try {
                const response = await axios.post('http://localhost:8080/api/upload', formData, {
                    headers: {
                        "Content-Type": "multipart/form-data"
                    }
                });
                setMetadata(response.data);  // GPS Ï†ïÎ≥¥ÏôÄ Î©îÌÉÄÎç∞Ïù¥ÌÑ∞ ÌëúÏãú
            } catch (error) {
                console.error("Î©îÌÉÄÎç∞Ïù¥ÌÑ∞ Ï∂îÏ∂ú Ïò§Î•ò:", error);
            }
        };

        fetchMetadata();
    }, [selectedFile]);

    return (
        <div>
            <h2>Ïù¥ÎØ∏ÏßÄ Î©îÌÉÄÎç∞Ïù¥ÌÑ∞ Ï∂îÏ∂ú</h2>
            <ChatContainer>
                {selectedFile && (
                <div>
                    <h3>üì∏ ÏóÖÎ°úÎìú Îêú Ïù¥ÎØ∏ÏßÄ</h3>
                    <img
                        src={URL.createObjectURL(selectedFile)}
                        alt="ÏóÖÎ°úÎìúÎêú Ïù¥ÎØ∏ÏßÄ"
                        style={{maxWidth: 300, marginBottom: 16}}
                    />
                </div>
            )}
                {metadata && (
                    <div>
                        <h3>üì∏ Ï∂îÏ∂úÎêú EXIF Î©îÌÉÄÎç∞Ïù¥ÌÑ∞</h3>
                        <pre>{JSON.stringify(metadata, null, 2)}</pre>
                    </div>
                )}
            <FloatingButton onClick={() => navigate("/map")}>
                ‚úàÔ∏è + ÏùºÏ†ï ÏÉùÏÑ±ÌïòÍ∏∞
            </FloatingButton>
            </ChatContainer>
        </div>
    );
}

const ChatContainer = styled.div`
  flex: 1;
  display: flex;
flex-direction: row; 
gap: 20px;
  padding: 20px;
  overflow-y: auto;
  position: relative; 
`;


const FloatingButton = styled.button`
  align-self: flex-end;
  margin: 50px 0;
  background-color: #000000;
  color: white;
  border: none;
  border-radius: 12px;
  padding: 12px 15px;
  font-size: 0.9rem;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: rgb(55, 58, 61);
  }
`;
export default Metadata;
