import React, { useState, useContext } from 'react';
import styled from 'styled-components';
import { FiMic, FiImage, FiSend } from 'react-icons/fi';
import { useNavigate } from "react-router-dom";
import { FileContext } from '../../contexts/FileContext';


const Header = () => {
    const { setSelectedFile } = useContext(FileContext);
    const [file, setFile] = useState(null);
    const navigate = useNavigate();
    const [searchTerm, setSearchTerm] = useState('');

    // 파일 선택
    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file && file.size > 10 * 1024 * 1024) {
            alert("10MB 이하의 이미지만 업로드할 수 있어요.");
            return;
        }
        setFile(file);
    };

    // FiSend 클릭 시 파일을 Context에 저장하고 Metadata 페이지로 이동
    const handleSend = async () => {
        if (!file) {
            alert("이미지 파일을 선택하세요.");
            return;
        }

        const resizedBlob = await resizeImage(file, 1000); // 최대 1000px로 리사이즈
        const resizedFile = new File([resizedBlob], file.name, {
            type: file.type
        });

        setSelectedFile(resizedFile); // 리사이즈된 파일 저장
        navigate("/"); // 메인 페이지로 이동
    };
    const resizeImage = (file, maxSize = 1000) => {
        return new Promise((resolve) => {
            const img = new Image();
            const reader = new FileReader();

            reader.onload = (e) => {
                img.src = e.target.result;
            };

            img.onload = () => {
                const canvas = document.createElement("canvas");
                let width = img.width;
                let height = img.height;

                // 비율 유지하면서 리사이즈
                if (width > height) {
                    if (width > maxSize) {
                        height *= maxSize / width;
                        width = maxSize;
                    }
                } else {
                    if (height > maxSize) {
                        width *= maxSize / height;
                        height = maxSize;
                    }
                }

                canvas.width = width;
                canvas.height = height;

                const ctx = canvas.getContext("2d");
                ctx.drawImage(img, 0, 0, width, height);

                canvas.toBlob((blob) => {
                    resolve(blob);
                }, file.type);
            };

            reader.readAsDataURL(file);
        });
    };


    return (
        <HeaderContainer>
            <InputWrapper>
                <Icon>
                    <label htmlFor="file-upload">
                        <FiImage style={{ cursor: "pointer" }} />
                    </label>
                    <input
                        id="file-upload"
                        type="file"
                        accept="image/*"
                        style={{ display: "none" }}
                        onChange={handleFileChange}
                    />
                </Icon>
                <FileName>
                    {file ? file.name : "이미지 파일을 선택하세요"}
                </FileName>
                <SendIcon onClick={handleSend}><FiSend /></SendIcon>
            </InputWrapper>
        </HeaderContainer>
    );
};


const HeaderContainer = styled.header`
    margin-top : 70px;
    display: flex;
    justify-content: center;
`;

const InputWrapper = styled.div`
    background: #f7f9fb;
    display: flex;
    align-items: center;
    border-radius: 14px;
    padding: 15px 15px;
    width: 100%;
    max-width: 900px;

    color: #f7f9fb; /* 초기 글자색 */
    border: 1px solid transparent;

    transition: border 0.3s ease-in-out, color 0.3s ease-in-out;

    &:hover {
        border: 1px solid #333333;
        color: #444444;
    }
`;
const FileName = styled.div`
    flex: 1;
    background: transparent;
    border: none;
    font-size : 0.8rem;
    color: #444;
    outline: none;
    padding: 0 10px;
`;


const Icon = styled.div`
    margin-right: 10px;
    font-size: 1rem;
    color: #555;
    cursor: pointer;
`;

const SearchInput = styled.input`
    flex: 1;
    background: transparent;
    border: none;
    font-size : 0.8rem;
    color: #444;
    outline: none;
    padding: 0 10px;

    &::placeholder {
        color: #ccc;
    }
`;

const SendIcon = styled.div`
    margin-left: 10px;
    font-size: 1rem;
    color: #888;
    cursor: pointer;

    &:hover {
        transform: scale(1.1);
        color: #444;
    }
`;


export default Header;
