import React, { useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from "react-router-dom";

const UNIT_OPTIONS = [
    { label: 'Ïó∞ÎèÑ', value: 'year' },
    { label: 'Ïõî', value: 'month' },
    { label: 'Ïùº', value: 'day' },
    { label: 'ÏãúÍ∞Ñ', value: 'time' },
];
const QUICK_RADIUS = [
    { label: 'Í∑ºÏ≤ò(1km)', value: 1 },
    { label: 'ÎßàÏùÑ(5km)', value: 5 },
    { label: 'Ïãú Îã®ÏúÑ(20km)', value: 20 },
    { label: 'ÎèÑ Îã®ÏúÑ(50km)', value: 50 }
];

const SEARCH_TYPE_OPTIONS = [
    { label: 'ÏúÑÏπò', value: 'location' },
    { label: 'ÏãúÍ∞Ñ', value: 'time' }
];


const UnitSelector = ({ onChange }) => {
    const [unit, setUnit] = useState('');
    const handleClick = (value) => {
        setUnit(value);
        if (onChange) onChange(value);
    };
    return (
        <QuickUnitWrap>
            {UNIT_OPTIONS.map(opt => (
                <QuickUnitBtn
                    key={opt.value}
                    active={unit === opt.value}
                    onClick={() => handleClick(opt.value)}
                >
                    {opt.label}
                </QuickUnitBtn>
            ))}
        </QuickUnitWrap>
    );
};


const MetaSearchFilter = ({ onFilterChange }) => {
    const [searchType, setSearchType] = useState(''); // 'location' or 'time'
    const [radius, setRadius] = useState(10);
    const [unit, setUnit] = useState('');
    const navigate = useNavigate();


    // Í≤ÄÏÉâ Í∏∞Ï§Ä Î≥ÄÍ≤Ω
    const handleSearchTypeChange = (e) => {
        setSearchType(e.target.value);
        // Ï¥àÍ∏∞Ìôî
        setRadius(10);
        setUnit('');
        if (onFilterChange) onFilterChange({ searchType: e.target.value });
    };

    // Î∞òÍ≤Ω Î≥ÄÍ≤Ω
    const handleRadiusChange = (e) => {
        const value = Number(e.target.value);
        setRadius(value);
        if (onFilterChange) onFilterChange({ searchType, radius: value });
    };

    // Îπ†Î•∏ ÏÑ†ÌÉù Î≤ÑÌäº ÌÅ¥Î¶≠
    const handleQuickRadius = (value) => {
        setRadius(value);
        if (onFilterChange) onFilterChange({ searchType, radius: value });
    };

    // Îã®ÏúÑ Î≥ÄÍ≤Ω
    const handleUnitChange = (selectedUnit) => {
        setUnit(selectedUnit);
        if (onFilterChange) onFilterChange({ searchType, unit: selectedUnit });
    };

    return (
        <div>
            <ResultWrap>
                <h1>üéõÔ∏è Í≤ÄÏÉâ ÌïÑÌÑ∞ ÏÑ§Ï†ïÌïòÍ∏∞</h1>
                <FilterCard>
                    <Section>
                        <Label>
                            üîé
                            Í≤ÄÏÉâ Í∏∞Ï§Ä ÏÑ†ÌÉù
                        </Label>
                        <div>
                            <label>
                                <input
                                    type="radio"
                                    name="searchType"
                                    value="location"
                                    checked={searchType === 'location'}
                                    onChange={handleSearchTypeChange}
                                /> ÏúÑÏπò
                            </label>
                            <label style={{ marginLeft: 16 }}>
                                <input
                                    type="radio"
                                    name="searchType"
                                    value="time"
                                    checked={searchType === 'time'}
                                    onChange={handleSearchTypeChange}
                                /> ÏãúÍ∞Ñ
                            </label>
                        </div>
                    </Section>

                    {searchType === 'location' && (
                        <Section>
                            <Label>üó∫Ô∏è ÏúÑÏπò Í∏∞Î∞ò Î∞òÍ≤Ω</Label>
                            <QuickUnitWrap>
                                {QUICK_RADIUS.map(opt => (
                                    <QuickUnitBtn
                                        key={opt.value}
                                        active={radius === opt.value}
                                        onClick={() => handleQuickRadius(opt.value)}
                                    >
                                        {opt.label}
                                    </QuickUnitBtn>
                                ))}
                            </QuickUnitWrap>
                            <RadiusRow>
                                <RadiusInput
                                    type="range"
                                    min={1}
                                    max={100}
                                    value={radius}
                                    onChange={handleRadiusChange}
                                    accentColor="#ff5a5f"
                                />
                                <RadiusValue>{radius} km</RadiusValue>
                            </RadiusRow>
                        </Section>
                    )}

                    {searchType === 'time' && (
                        <Section>
                            <Label>
                                üìÖ Í≤ÄÏÉâÌï† ÏãúÍ∞Ñ Î≤îÏúÑ
                            </Label>
                            <UnitSelector onChange={handleUnitChange} />
                        </Section>
                    )}
                </FilterCard>
            </ResultWrap>
            <ButtonContainer>
                <FloatingButton onClick={() => navigate("/metasearch")}>
                    üì∏ + Ïù¥ÎØ∏ÏßÄ Í≤ÄÏÉâÌïòÍ∏∞
                </FloatingButton>
            </ButtonContainer>
        </div>
    );
};

const ResultWrap = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: center;
    margin-top: 30px;
`;


const FilterCard = styled.div`
    display: flex;
    flex-direction: column;
    gap: 24px;
    padding: 32px 28px 24px 28px;
    background: white;
    border-radius: 20px;
    box-shadow: 0 8px 24px rgba(74, 144, 226, 0.08), 0 1.5px 4px rgba(0,0,0,0.03);
    border: 1px solid #e0e7ef;
    max-width: 660px;
    //min-width: 320px;
    width: 100%;
`;

const Section = styled.div`
    display: flex;
    flex-direction: column;
`;

const Label = styled.label`
    font-weight: 600;
    margin-bottom: 10px;
    color: #333;
    font-size: 1.08rem;
    display: flex;
    align-items: center;
`;



const RadiusRow = styled.div`
    display: flex;
    align-items: center;
    gap: 16px;
`;

const RadiusInput = styled.input`
    width: 100%;
    accent-color: #ff5a5f;
`;

const RadiusValue = styled.div`
    min-width: 50px;
    font-size: 1rem;
    color: #ff5a5f;
    font-weight: 700;
    text-align: right;
`;

const ButtonContainer = styled.div`
    display: flex;
    flex-direction: column; 
    align-items: flex-end;
    padding: 20px 40px 0 0;
`;

const FloatingButton = styled.button`
    align-self: flex-end;
    margin: 50px 0;
    background-color: #000000;
    color: white;
    border: none;
    border-radius: 12px;
    padding: 12px 15px;
    font-size: 0.9rem;
    cursor: pointer;
    transition: background-color 0.3s ease;

    &:hover {
        background-color: rgb(55, 58, 61);
    }
`;


// Îπ†Î•∏ÏÑ§Ï†ï
const QuickUnitWrap = styled.div`
    display: flex;
    gap: 8px;
    margin-bottom: 10px;
`;

const QuickUnitBtn = styled.button`
    background: ${({ active }) => (active ? '#4a90e2' : '#f1f4fa')};
    color: ${({ active }) => (active ? 'white' : '#333')};
    border: none;
    border-radius: 8px;
    padding: 6px 14px;
    font-size: 0.95rem;
    cursor: pointer;
    transition: background 0.2s;
    &:hover {
        background: #4a90e2;
        color: white;
    }
`;

export default MetaSearchFilter;
