import { useNavigate, useLocation } from "react-router-dom"; // useLocation 추가

const Header = () => {
    const { setSelectedFile } = useContext(FileContext);
    const [file, setFile] = useState(null);
    const navigate = useNavigate();
    const location = useLocation(); // 현재 경로 확인

    const isRoot = location.pathname === "/"; // 현재 경로가 "/"인지 확인

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file && file.size > 10 * 1024 * 1024) {
            alert("10MB 이하의 이미지만 업로드할 수 있어요.");
            return;
        }
        setFile(file);
    };

    const handleSend = () => {
        if (!file) {
            alert("이미지 파일을 선택하세요.");
            return;
        }
        setSelectedFile(file);
        navigate("/");
    };

    return (
        <HeaderContainer>
            <InputWrapper>
                {isRoot ? (
                    <>
                        <Icon>
                            <label htmlFor="file-upload">
                                <FiImage />
                                <span>이미지 불러오기</span>
                            </label>
                            <input
                                id="file-upload"
                                type="file"
                                accept="image/*"
                                style={{ display: "none" }}
                                onChange={handleFileChange}
                            />
                        </Icon>
                        <FileName>
                            {file ? file.name : "이미지 파일을 선택하세요"}
                        </FileName>
                        <SendIcon
                            onClick={handleSend}
                            active={!!file}
                            title={file ? "업로드" : "이미지 파일을 선택하세요"}
                        >
                            <FiSend />
                        </SendIcon>
                    </>
                ) : (
                    <FileName>이미지 검색은 홈에서만 가능해요.</FileName>
                )}
            </InputWrapper>
        </HeaderContainer>
    );
};
