import {useEffect, useState} from "react";
import axios from "axios";

function Metadata() {

    const [selectedFile, setSelectedFile] = useState(null);
    const [metadata, setMetadata] = useState(null);

    // ÌååÏùº ÏÑ†ÌÉù Ìï∏Îì§Îü¨
    const handleFileChange = (event) => {
        setSelectedFile(event.target.files[0]);
    };

    // ÌååÏùº ÏóÖÎ°úÎìú Î∞è Î©îÌÉÄÎç∞Ïù¥ÌÑ∞ ÏöîÏ≤≠
    const handleUpload = async () => {
        if (!selectedFile) {
            alert("ÌååÏùºÏùÑ ÏÑ†ÌÉùÌïòÏÑ∏Ïöî.");
            return;
        }

        const formData = new FormData();
        formData.append("file", selectedFile);

        try {
            const response = await axios.post("http://localhost:8080/api/upload", formData, {
                headers: {
                    "Content-Type": "multipart/form-data"
                }
            });

            setMetadata(response.data);
        } catch (error) {
            console.error("Î©îÌÉÄÎç∞Ïù¥ÌÑ∞ Ï∂îÏ∂ú Ïò§Î•ò:", error);
        }
    };

    return (
        <div>
            <h2>Ïù¥ÎØ∏ÏßÄ Î©îÌÉÄÎç∞Ïù¥ÌÑ∞ Ï∂îÏ∂ú</h2>
            <input type="file" accept="image/*" onChange={handleFileChange} />
            <button onClick={handleUpload}>ÏóÖÎ°úÎìú</button>

            {metadata && (
                <div>
                    <h3>üì∏ Î™®Îì† EXIF Î©îÌÉÄÎç∞Ïù¥ÌÑ∞</h3>
                    <pre>{JSON.stringify(metadata, null, 2)}</pre>
                </div>
            )}
        </div>
    );
}

export default Metadata;
