import { useContext, useEffect, useState } from "react";
import axios from "axios";
import { FileContext } from "../contexts/FileContext";

function Metadata() {
    const { selectedFile } = useContext(FileContext);
    const [metadata, setMetadata] = useState(null);

    useEffect(() => {
        if (!selectedFile) return;

        const formData = new FormData();
        formData.append("file", selectedFile);

        // ÎπÑÎèôÍ∏∞ Ìï®Ïàò ÏÑ†Ïñ∏ ÌõÑ Ï¶âÏãú Ïã§Ìñâ
        const fetchMetadata = async () => {
            try {
                const response = await axios.post('http://localhost:8080/api/upload', formData, {
                    headers: {
                        "Content-Type": "multipart/form-data"
                    }
                });
                setMetadata(response.data);  // GPS Ï†ïÎ≥¥ÏôÄ Î©îÌÉÄÎç∞Ïù¥ÌÑ∞ ÌëúÏãú
            } catch (error) {
                console.error("Î©îÌÉÄÎç∞Ïù¥ÌÑ∞ Ï∂îÏ∂ú Ïò§Î•ò:", error);
            }
        };

        fetchMetadata();
    }, [selectedFile]);

    return (
        <div>
            <h2>Ïù¥ÎØ∏ÏßÄ Î©îÌÉÄÎç∞Ïù¥ÌÑ∞ Ï∂îÏ∂ú</h2>
            {selectedFile && (
                <div>
                    <h3>üì∏ ÏóÖÎ°úÎìú Îêú Ïù¥ÎØ∏ÏßÄ</h3>
                    <img
                        src={URL.createObjectURL(selectedFile)}
                        alt="ÏóÖÎ°úÎìúÎêú Ïù¥ÎØ∏ÏßÄ"
                        style={{maxWidth: 300, marginBottom: 16}}
                    />
                </div>
            )}
            {metadata && (
                <div>
                    <h3>üì∏ Ï∂îÏ∂úÎêú EXIF Î©îÌÉÄÎç∞Ïù¥ÌÑ∞</h3>
                    <pre>{JSON.stringify(metadata, null, 2)}</pre>
                </div>
            )}
        </div>
    );
}

export default Metadata;
