import { useState, useEffect } from "react";
import axios from "axios";
import { useLocation } from "react-router-dom";

function MetadataGen() {
    const [metadata, setMetadata] = useState(null);
    const location = useLocation();

    // ÏøºÎ¶¨Ïä§Ìä∏ÎßÅÏóêÏÑú Ïù¥ÎØ∏ÏßÄ ÎßÅÌÅ¨ Ï∂îÏ∂ú
    const query = new URLSearchParams(location.search);
    const imgUrl = query.get("imgUrl");

    useEffect(() => {
        if (imgUrl) {
            // ÏÑúÎ≤ÑÏóê Ïù¥ÎØ∏ÏßÄ ÎßÅÌÅ¨Î°ú Î©îÌÉÄÎç∞Ïù¥ÌÑ∞ ÏöîÏ≤≠
            axios.post('http://localhost:8080/api/upload-url', { url: imgUrl })
                .then(res => setMetadata(res.data))
                .catch(err => console.error("Î©îÌÉÄÎç∞Ïù¥ÌÑ∞ Ï∂îÏ∂ú Ïò§Î•ò:", err));
        }
    }, [imgUrl]);

    return (
        <div>
            <h2>Ïù¥ÎØ∏ÏßÄ Î©îÌÉÄÎç∞Ïù¥ÌÑ∞ Ï∂îÏ∂ú</h2>
            {imgUrl && (
                <div>
                    <img src={imgUrl} alt="ÏóÖÎ°úÎìúÎêú Ïù¥ÎØ∏ÏßÄ" style={{ maxWidth: 300, marginBottom: 16 }} />
                </div>
            )}
            {metadata && (
                <div>
                    <h3>üì∏ Î™®Îì† EXIF Î©îÌÉÄÎç∞Ïù¥ÌÑ∞</h3>
                    <pre>{JSON.stringify(metadata, null, 2)}</pre>
                </div>
            )}
        </div>
    );
}

export default MetadataGen;
