import React, { useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from "react-router-dom";

// Îπ†Î•∏ Îã®ÏúÑ ÏÑ†ÌÉù Î≤ÑÌäºÏö© ÏòµÏÖò
const UNIT_OPTIONS = [
    { label: 'Ïó∞ÎèÑ', value: 'year' },
    { label: 'Ïõî', value: 'month' },
    { label: 'Ïùº', value: 'day' },
    { label: 'ÏãúÍ∞Ñ', value: 'time' },
];

// Îπ†Î•∏ Î∞òÍ≤Ω ÏÑ†ÌÉù Î≤ÑÌäºÏö© ÏòµÏÖò
const QUICK_RADIUS = [
    { label: 'Í∑ºÏ≤ò(1km)', value: 1 },
    { label: 'ÎßàÏùÑ(5km)', value: 5 },
    { label: 'Ïãú Îã®ÏúÑ(20km)', value: 20 },
    { label: 'ÎèÑ Îã®ÏúÑ(50km)', value: 50 }
];

// Îπ†Î•∏ Í∏∞Ï§Ä(ÏúÑÏπò/ÏãúÍ∞Ñ) ÏÑ†ÌÉù Î≤ÑÌäºÏö© ÏòµÏÖò
const SEARCH_TYPE_OPTIONS = [
    { label: 'ÏúÑÏπò', value: 'location' },
    { label: 'ÏãúÍ∞Ñ', value: 'time' }
];

// Î≤ÑÌäºÌòï UnitSelector
const UnitSelector = ({ onChange }) => {
    const [unit, setUnit] = useState('');
    const handleClick = (value) => {
        setUnit(value);
        if (onChange) onChange(value);
    };
    return (
        <QuickBtnWrap>
            {UNIT_OPTIONS.map(opt => (
                <QuickBtn
                    key={opt.value}
                    active={unit === opt.value}
                    onClick={() => handleClick(opt.value)}
                >
                    {opt.label}
                </QuickBtn>
            ))}
        </QuickBtnWrap>
    );
};

// Î≤ÑÌäºÌòï SearchTypeSelector
const SearchTypeSelector = ({ value, onChange }) => (
    <QuickBtnWrap>
        {SEARCH_TYPE_OPTIONS.map(opt => (
            <QuickBtn
                key={opt.value}
                active={value === opt.value}
                onClick={() => onChange(opt.value)}
            >
                {opt.label}
            </QuickBtn>
        ))}
    </QuickBtnWrap>
);

const MetaSearchFilter = ({ onFilterChange }) => {
    const [searchType, setSearchType] = useState('');
    const [radius, setRadius] = useState(10);
    const [unit, setUnit] = useState('');
    const navigate = useNavigate();

    // Í≤ÄÏÉâ Í∏∞Ï§Ä Î≥ÄÍ≤Ω
    const handleSearchTypeChange = (selectedType) => {
        setSearchType(selectedType);
        setRadius(10);
        setUnit('');
        if (onFilterChange) onFilterChange({ searchType: selectedType });
    };

    // Î∞òÍ≤Ω Î≥ÄÍ≤Ω
    const handleRadiusChange = (e) => {
        const value = Number(e.target.value);
        setRadius(value);
        if (onFilterChange) onFilterChange({ searchType, radius: value });
    };

    // Îπ†Î•∏ Î∞òÍ≤Ω ÏÑ†ÌÉù
    const handleQuickRadius = (value) => {
        setRadius(value);
        if (onFilterChange) onFilterChange({ searchType, radius: value });
    };

    // Îã®ÏúÑ Î≥ÄÍ≤Ω
    const handleUnitChange = (selectedUnit) => {
        setUnit(selectedUnit);
        if (onFilterChange) onFilterChange({ searchType, unit: selectedUnit });
    };

    return (
        <div>
            <ResultWrap>
                <h1>üéõÔ∏è Í≤ÄÏÉâ ÌïÑÌÑ∞ ÏÑ§Ï†ïÌïòÍ∏∞</h1>
                <FilterCard>
                    <Section>
                        <Label>üîé Í≤ÄÏÉâ Í∏∞Ï§Ä ÏÑ†ÌÉù</Label>
                        <SearchTypeSelector value={searchType} onChange={handleSearchTypeChange} />
                    </Section>

                    {searchType === 'location' && (
                        <Section>
                            <Label>üó∫Ô∏è ÏúÑÏπò Í∏∞Î∞ò Î∞òÍ≤Ω</Label>
                            <QuickBtnWrap>
                                {QUICK_RADIUS.map(opt => (
                                    <QuickBtn
                                        key={opt.value}
                                        active={radius === opt.value}
                                        onClick={() => handleQuickRadius(opt.value)}
                                    >
                                        {opt.label}
                                    </QuickBtn>
                                ))}
                            </QuickBtnWrap>
                            <RadiusRow>
                                <RadiusInput
                                    type="range"
                                    min={1}
                                    max={100}
                                    value={radius}
                                    onChange={handleRadiusChange}
                                    accentColor="#ff5a5f"
                                />
                                <RadiusValue>{radius} km</RadiusValue>
                            </RadiusRow>
                        </Section>
                    )}

                    {searchType === 'time' && (
                        <Section>
                            <Label>üìÖ Í≤ÄÏÉâÌï† ÏãúÍ∞Ñ Î≤îÏúÑ</Label>
                            <UnitSelector onChange={handleUnitChange} />
                        </Section>
                    )}
                </FilterCard>
            </ResultWrap>
            <ButtonContainer>
                <FloatingButton onClick={() => navigate("/metasearch")}>
                    üì∏ + Ïù¥ÎØ∏ÏßÄ Í≤ÄÏÉâÌïòÍ∏∞
                </FloatingButton>
            </ButtonContainer>
        </div>
    );
};

// Ïä§ÌÉÄÏùº Ï†ïÏùò
const ResultWrap = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: center;
    margin-top: 30px;
`;

const FilterCard = styled.div`
    display: flex;
    flex-direction: column;
    gap: 24px;
    padding: 32px 28px 24px 28px;
    background: white;
    border-radius: 20px;
    box-shadow: 0 8px 24px rgba(74, 144, 226, 0.08), 0 1.5px 4px rgba(0,0,0,0.03);
    border: 1px solid #e0e7ef;
    max-width: 660px;
    width: 100%;
`;

const Section = styled.div`
    display: flex;
    flex-direction: column;
`;

const Label = styled.label`
    font-weight: 600;
    margin-bottom: 10px;
    color: #333;
    font-size: 1.08rem;
    display: flex;
    align-items: center;
`;

const QuickBtnWrap = styled.div`
    display: flex;
    gap: 8px;
    margin-bottom: 10px;
`;

const QuickBtn = styled.button`
    background: ${({ active }) => (active ? '#e47171' : '#f1f4fa')};
    color: ${({ active }) => (active ? 'white' : '#333')};
    border: none;
    border-radius: 8px;
    padding: 6px 14px;
    font-size: 0.95rem;
    cursor: pointer;
    transition: background 0.2s;
    &:hover {
        background: #4a90e2;
        color: white;
    }
`;

const RadiusRow = styled.div`
    display: flex;
    align-items: center;
    gap: 16px;
`;

const RadiusInput = styled.input`
    width: 100%;
    accent-color: #ff5a5f;
`;

const RadiusValue = styled.div`
    min-width: 50px;
    font-size: 1rem;
    color: #ff5a5f;
    font-weight: 700;
    text-align: right;
`;

const ButtonContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: flex-end;
    padding: 20px 40px 0 0;
`;

const FloatingButton = styled.button`
  align-self: flex-end;
  margin: 50px 0;
  background-color: #000000;
  color: white;
  border: none;
  border-radius: 12px;
  padding: 12px 15px;
  font-size: 0.9rem;
  cursor: pointer;
  transition: background-color 0.3s ease;
  &:hover {
    background-color: rgb(55, 58, 61);
  }
`;

export default MetaSearchFilter;
